function initializeSchemeSwitching() {
    
  // START IMAGE PRELOADING
  function preload(arrayOfImages) {
    $(arrayOfImages).each(function(){
        $('<img/>')[0].src = this;
    });
  }

  preload([
    '/assets/home_page/home-page-banner-image-1-6fdb2ee1b400c392a0be8fb1b143dec0c27c54b58d3d935b9689ac9dc893b490.png',
    '/assets/home_page/home-page-banner-background-image-1-3a9950953fdb00f29ff787159b6b68ba618bf8a669616c3cf29080b097f1e8a5.jpg',
    '/assets/home_page/home-page-banner-image-2-e5bd875ce01acc2dd9e352b2e84651c097c4a087a8b677ea1f4e233b6755d0a6.png',
    '/assets/home_page/home-page-banner-background-image-2-ed4780deab150e0a4931c3f1af2fb830db162c7c881c9b17810bad6cdb0ec018.jpg'
  ]);
  // END IMAGE PRELOADING

  // START TIMING COUNTER
  // run a new step every 10 seconds
  var counter = 1;
  var int = setInterval(function(){
    if (counter === 2) {
      advanceSliderByOneSlide();
      switchScheme(counter);
      counter = 1; // If counter = 2, set it back to 1 for next loop
    } else {
      advanceSliderByOneSlide();
      switchScheme(counter);
      counter++; // Else, add 1 to the counter
    }
  }, 10000);
  // END TIMING COUNTER

  function advanceSliderByOneSlide() {
    var slides = $('.slide');
    var activeSlide = $('.active');
    slides.last().after(slides.first());
    // Move active class to the right
    activeSlide.removeClass('active').next('.slide').addClass('active');
  }

  function switchScheme(counter) {
    var scheme_number = counter;
    // remove any class from body that starts with "scheme-"
    $('body').removeClass(function(index,classes){
        var matches = classes.match(/\bscheme-\S+/ig);
        return (matches) ? matches.join(' ') : '';
    });
    $('body').addClass('scheme-' + scheme_number);
  }
}
